for(var buyLink=document.querySelectorAll(".buy-button"),succesPopup=document.querySelectorAll(".modal-cart-success"),succesClose=document.querySelectorAll(".modal-close"),continueBuy=document.querySelectorAll(".continue-button"),i=0;i<buyLink.length;i++)buyLink[i].addEventListener("click",function(e){e.preventDefault(),succesPopup[0].classList.add("modal-show")});for(i=0;i<succesClose.length;i++)succesClose[i].addEventListener("click",function(e){e.preventDefault(),succesPopup[0].classList.remove("modal-show")});for(i=0;i<continueBuy.length;i++)continueBuy[i].addEventListener("click",function(e){e.preventDefault(),succesPopup[0].classList.remove("modal-show")});window.addEventListener("keydown",function(e){27===e.keyCode&&succesPopup[0].classList.contains("modal-show")&&(e.preventDefault(),succesPopup[0].classList.remove("modal-show"))});